// App.js
import React, { useState, useEffect } from 'react';
import Overview from './Overview';
import ProductDetails from './ProductDetails';
import Cart from './Cart';

const App = () => {
  const [cartItems, setCartItems] = useState([]);
  const [currentView, setCurrentView] = useState('overview');

  // Function to handle navigation
  const navigateTo = (view) => {
    setCurrentView(view);
  };

  // Function to determine the current view based on the URL
  const determineCurrentView = () => {
    const path = window.location.pathname.toLowerCase();

    if (path.startsWith('/product')) {
      return 'product-details';
    } else if (path === '/cart') {
      return 'cart';
    } else {
      return 'overview';
    }
  };

  // Example of how you might handle navigation based on URL using useEffect
  useEffect(() => {
    setCurrentView(determineCurrentView());
  }, []);

  return (
    <div>
      {currentView === 'overview' && <Overview navigateTo={navigateTo} />}
      {currentView === 'product-details' && <ProductDetails />}
      {currentView === 'cart' && <Cart cartItems={cartItems} />}
    </div>
  );
};

export default App;
